@app.route('/')
def home():
    return render_template('home.html')

@app.route('/letters')
def letters():
    return render_template('letters.html', letters=letters_list)

@app.route('/recommendations')
def recommendations_page():
    return render_template('recommendations.html')


@app.route('/home_seek')
def home_seek():
    return render_template('home_seek.html', home_seek_items= home_seek_items)

@app.route('/letters/<int:letter_id>')
def letter_detail(letter_id):
    letter = next((letter for letter in letters_list if letter['id'] == letter_id), None)
    if letter is None:
        return "Letter not found", 404
    return render_template('letter_detail.html', letter=letter)

if __name__ == '__main__':
    app.run(debug=True)
